	/**
	 * Use this method to enter the Saved Search details on various pages of it
	 * @param driver
	 * @param savedSearchName
	 * @param visibityOptionToSelect
	 * @param notificationOptionToSelect
	 * @param numberOfLibrariesToSelect
	 */
	public List<String> enterSavedSearchDetails(WebDriver driver, String savedSearchName, visibityOptions visibityOptionToSelect, notificationOptions notificationOptionToSelect, int numberOfLibrariesToSelect) {
		List<String> libraryList = new ArrayList<>();
		
		verifyCreateSavedSrchVisibilityOptnsPage(driver);
		
		enterText(driver, newSavedSrchNameBox, savedSearchName);
		selectSaveSearchVisibilityOption(driver, visibityOptionToSelect);
		clickElement(driver, visibilityOptionsNextBtn);
		waitUntilVisible(driver, notificationOptionsNextBtn);
		
		verifyCreateSavedSrchNotificationOptnsPage(driver);
		selectSavedSearchNotificationOption(driver, notificationOptionToSelect);
		clickElement(driver, notificationOptionsNextBtn);
		waitUntilVisible(driver, savedSearchSaveBtn);
		
		libraryList = selectSavedSearchLibrary(driver, numberOfLibrariesToSelect);
		clickElement(driver, savedSearchSaveBtn);
		
		waitUntilVisible(driver, toastMessage);
		assertEquals(getElementsText(driver, toastMessage), "Search saved.");
		isElementInvisible(driver, toastMessage, 5);
		
		return libraryList;
	}
	
